int SliderW::handle(int event) {
  if (event == FL_PUSH && Fl::visible_focus()) {
      Fl::focus(this);
      redraw();
  }  
    
    int sxx = x(), syy = y(), sww = w(), shh = h();
    
    if (horizontal()) {
      sxx += 35; sww -= 35;
    } else {
      syy += 25; shh -= 25;
   }
    
   
    
   return handle2(event,
                    sxx+Fl::box_dx(box()),
                    syy+Fl::box_dy(box()),
                    sww-Fl::box_dw(box()),
                    shh-Fl::box_dh(box()));
}

int SliderW::handle2(int event, int X, int Y, int W, int H) {
  int mul=1;
  switch (event) {
    case FL_PUSH:
      if (!Fl::event_inside(X, Y, W, H)) return 0;
       handle_push();
    case FL_DRAG: {
      if(Fl::event_button()==3) return 1;
      double val;
      if (minimum() == maximum())
        val = 0.5;
      else {
        val = (value()-minimum())/(maximum()-minimum());
        if (val > 1.0) val = 1.0;
        else if (val < 0.0) val = 0.0;
      }
  
      int ww = (horizontal() ? W : H);
      int mx = (horizontal() ? Fl::event_x()-X : Fl::event_y()-Y);
      int S=0;
      static int offcenter;
  
     
        S = int(0*ww+.5); if (S >= ww) return 0;
        int T = (horizontal() ? H : W)/2+1;
        T += 4;
        if (S < T) S = T;
        if (event == FL_PUSH) {
         int xx = int(val*(ww-S)+.5);
        
          offcenter = mx-xx;
          if (offcenter < 0) offcenter = 0;
          else if (offcenter > S) offcenter = S;
          else return 1;
        }
      
  
      int xx = mx-offcenter;
      double v;
      char tryAgain = 1;
      while (tryAgain)
      {
        tryAgain = 0;
        if (xx < 0) {
          xx = 0;
          offcenter = mx; if (offcenter < 0) offcenter = 0;
        } else if (xx > (ww-S)) {
          xx = ww-S;
          offcenter = mx-xx; if (offcenter > S) offcenter = S;
        }
          v = round(xx*(maximum()-minimum())/(ww-S) + minimum());
          
          
          // make sure a click outside the sliderbar moves it:
        if (event == FL_PUSH && v == value()) {
          offcenter = S/2;
          event = FL_DRAG;
          tryAgain = 1;
        }
      }
  
  
     handle_drag(clamp(v));
      } return 1;
   
      case FL_MOUSEWHEEL :
    
        if (Fl::e_dy==0) return 0;
        handle_push();
        handle_drag(clamp(increment(value(),Fl::e_dy)));
        handle_release();
        return 1;
      
   
    case FL_RELEASE:
      
      handle_release();
      return 1;
    case FL_KEYBOARD :
         if (Fl::event_state(FL_SHIFT)) mul=10; else 
         if (Fl::event_state(FL_CTRL)) mul=100; else
         mul = 1;
          
        switch (Fl::event_key()) {
        case FL_Up:
          if (horizontal()) return 0;
          handle_push();
          handle_drag(clamp(increment(value(),-1*mul)));
          handle_release();
          return 1;
        case FL_Down:
          if (horizontal()) return 0;
          handle_push();
          handle_drag(clamp(increment(value(),1*mul)));
          handle_release();
          return 1;
        case FL_Left:
          if (!horizontal()) return 0;
          handle_push();
          handle_drag(clamp(increment(value(),-1*mul)));
          handle_release();
          return 1;
        case FL_Right:
          if (!horizontal()) return 0;
          handle_push();
          handle_drag(clamp(increment(value(),1*mul)));
          handle_release();
          return 1;
        default:
          return 0;
      }
      // break not required because of switch...
    case FL_FOCUS :
    case FL_UNFOCUS :
      if (Fl::visible_focus()) {
        redraw();
        return 1;
      } else return 0;
    case FL_ENTER :
    case FL_LEAVE :
      return 1;
    default:
      return 0;
    }
}

